mapper.select:

 SELECT COUNT(*)
 FROM (
  SELECT *
FROM facility_rental
  LIMIT #{nextIndex}, #{rows}
  ) as l

mapper.update:
UPDATE facility_rental
   SET title = #{title}
      ,content = #{content}
      ,price = #{price}
      ,location = #{location}
      ,address = #{address}
      ,live_date = #{liveDate}
      ,account = #{account}
WHERE fr_no = #{frNo}
